import React, { Component } from "react";
import BrowseCard from '../components/browseCard';
import './recipes.sass';
import Footer from '../components/footer';
import axios from 'axios';

// added task. Pagination using random recipes.


class Recipes extends Component {
	constructor(props) {
		super(props);
		this.state = {
			cardData: [],
			isLoaded: false,
			loadFooter: false,
			atBottom: false,
			height: window.innerHeight
		}
		this.handleScroll = this.handleScroll.bind(this);
		this.loadMoreRecipes = this.loadMoreRecipes.bind(this);
	}

	componentDidMount() {
		window.addEventListener("scroll", this.handleScroll);

		axios.get("/api/recipes/")
			.then(res => {
					this.setState({
						cardData: res.data,
						isLoaded: true,
						loadFooter: true
					});
				}
			)
	}

	loadMoreRecipes() {
		axios.get("/api/recipes/")
			.then(res => {
					this.setState(prevState => ({
						cardData: prevState.cardData.concat(res.data)
					}), console.log(this.state.cardData));
				}
			)
	}

	componentWillUnmount() {
	    window.removeEventListener("scroll", this.handleScroll);
	}


	handleScroll() {
        const windowHeight = "innerHeight" in window ? window.innerHeight : document.documentElement.offsetHeight;
        const body = document.body;
        const html = document.documentElement;
        const docHeight = Math.max(body.scrollHeight, body.offsetHeight, html.clientHeight, html.scrollHeight, html.offsetHeight);
        const windowBottom = windowHeight + window.pageYOffset;
        if (windowBottom >= docHeight ) {
            console.log('bottom');
            this.loadMoreRecipes();
        }
    }


	render() {
		const {isLoaded } = this.state;


		if (!isLoaded) {
			return null;
		} else {
			const { cardData } = this.state;
			return (
				<div className='browse-body'>
					<div className='recipe-grid-container' >
						{cardData.map((cardData, index) => 
							<BrowseCard 
								key={index} 
								img={cardData.img} 
								description={cardData.description} 
								author={cardData.authorName} 
								rtitle={cardData.title} />	
						)}
					</div>
					{/*<Footer isLoaded={this.state.loadFooter} />*/}
				</div>
			);
		}
	}
}






export default Recipes;
