import React, { Component, useState } from 'react';
import { Link } from "react-router-dom";
import './forgot-password.sass';
import axios from 'axios';
import PropTypes from "prop-types";


function Forgotpassword() {

	const [email, setEmail] = useState('');
	const [msg, setMsg] = useState('');

	function handleMsg(msg) {
		if (msg.length > 0) {
			return <div className='logMsg'>{msg}</div>;
		}
	}

	function sendEmail(email) {
		const data = JSON.stringify({email: email, check: 'check'});
		axios.post('/api/mail/pwd-reset', data)
			.then(res => {
				//console.log(res.data);
				setMsg(res.data);
			})
			.catch(err => {
				console.log(err)
			})
	}

	return (
		<div className="reset-container">
			<div id="resetTitle">Password Reset</div>
			<div id="resetMsg">
				Enter the email of your account. We will then send you a link to reset your password.
			</div>
			<input 
				type="text" 
				name="email" 
				placeholder="Enter your email" 
				onChange={e => setEmail(e.target.value)}
				/>
			<input 
				className="hp" 
				type="text" 
				name="email_two" 
				placeholder="Enter your email" 
				/>
			<button type="submit" name="reset-request-submit" onClick={() => sendEmail(email)}>Send password reset</button>
			{ handleMsg(msg) }
		</div>
	);
}


export default Forgotpassword;
