import React, { Component, useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import './login-page.sass';
import { withRouter } from "react-router";
import PropTypes from "prop-types";
import axios from 'axios';

// tried using functionalc component insead of class component to complete a form. Just for kicks



function Loginpage(props) {

	const [email, setEmail] = useState('');
	const [pwd, setPwd] = useState('');
	const [msg, setMsg] = useState('');

	function handleMsg(msg) {
		if (msg.length > 0) {
			const timer = setTimeout(() => {
				setMsg(msg);
			}, 5500);
			return <div id='loginMsg'>{msg}</div>; // output msg
		}
	}


	function login(email, pwd) {
		// const postOptions = {
		//         method: 'POST',
		//         headers: { 'Content-Type': 'application/json' },
		//         body: JSON.stringify({
		//         	email: email,
		//         	password: pwd
		//         })
		//     };

		// fetch('/api/users/login', postOptions)
		// 	.then(res => res.json())
		// 	.then(data => {
		// 		localStorage['logged_in'] = true;
		// 		props.login();
		// 		props.history.push('/'); // redirect using withRouter and prototypes import
		// 	})
		// 	.catch((error) => console.log(error));


		const data = JSON.stringify({ email: email, password: pwd });

		// axios.post('/api/users/login', data, { headers: {'Content-Type': 'application/json'} } )
		// 	.then(res => {
		// 		console.log('res');
		// 		localStorage['logged_in'] = true;
		// 		props.login();
		// 		props.history.push('/'); // redirect using withRouter and prototypes import
		// 	})
		// 	.catch(error => {
		// 		if (typeof error.response.data !== 'undefined') setMsg(error.response.data);
		// 	})

		axios.post('/api/users/login', data)
			.then(res => {
				console.log('res');
				// localStorage['logged_in'] = true;
				// props.login();
				// props.history.push('/'); // redirect using withRouter and prototypes import
			})
			.catch(error => {
				if (typeof error.response.data !== 'undefined') setMsg(error.response.data);
			})
	}


	return (
		<React.Fragment>
			<div className="loginContainer">
				{ handleMsg(msg) }
				<input 
					type="text" 
					name="email" placeholder="Email"
					value={email}
					onChange={e => setEmail(e.target.value)}
				/>
				<input 
					className="hp" 
					type="text" name="email_two" placeholder="Email"
				/>
				<input 
					type="Password" 
					name="pwd" 
					placeholder="Password"
					value={pwd}
					onChange={e => setPwd(e.target.value)}
				/>
				<button 
					onClick={() => login(email, pwd)} 
					id="logInBtn" 
					type="submit" 
					name="submit-loginPage">Log in
				</button>
				<div id="forgotPwd">
					<Link to="/forgot-passsword">Forgot Password</Link>
				</div>
				<div id="signUp">
					Don't have an account? <Link to="/signup-page">Sign up</Link>
				</div>
			</div>
		</React.Fragment>
	);
}


const LoginpageWithRouter = withRouter(Loginpage);


export default LoginpageWithRouter;
