	if (num1 === NaN || num2 === NaN || index === -1) {
		return string;
	}


	// seperate string components
	const firstHalf = string.substring(0, index-1).replace(' ', '');
	const secondHalf = string.substring(index+2);

	// console.log(string + ': ' + firstHalf + '|' + secondHalf);

	// determine unicode value
	// const uniCode = [188, 189, 190, 8528, 8529,8530,8531,8532,8533,8534,8535,8536,8537,8538,8539,8540,8541,8542];

	if (num1 === 1 && num2 === 4) {
		var unicode = 188;
	} else if (num1 === 1 && num2 === 2) {
