	} else if (num1 === 1 && num2 === 9) {
		var unicode = 8529;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8530;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8531;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8532;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8533;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8534;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8535;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8536;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8537;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8538;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8539;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8540;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8541;
	} else if (num1 === 1 && num2 === 4) {
		var unicode = 8542; 
	} else {
		return string;
	}


	return firstHalf + String.fromCharCode(unicode) + secondHalf;
}




export default Recipepage;
