import React, { Component, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './cookBanner.sass';
//import photoIcon from '../../public/user_profile_img/CHange_Name_16246155695775.jpeg';

const CookBanner = (props) => {
	const [recipeData, setRecipeData] = useState(null);
	const [isLoaded, setIsLoaded] = useState(false);

	useEffect(() => {

		const data = JSON.stringify({
			authName: props.authName, 
			currentRecipe: props.currentRecipe
		});

		axios.post('/api/recipe/get-cook-banner', data, { headers: {'Content-Type': 'application/json'} })
		.then((res) => {
			setRecipeData(res.data);
		})
		.catch((err) => {
			console.log(err);
		})
	})

	if (!isLoaded) {
		return null;
	} else {
		return(
			<div >
				<div id='author-card'>
					<Link to='#'>
						<img src={process.env.PUBLIC_URL + '/user_profile_img/' + props.profileImg}/>
					</Link>
				</div>
				<div id='name'></div>
				<div id='contents'>
					<div id='otherBy'></div>
					<div id='arrow-down'></div>
					<div id='moreByAuthor-load-container'>
						{ 	
							recipeData.map((recipe, index) => {
								<div class='more-banner-card' key={index}>
									<Link to='#'>
										<img src={process.env.PUBLIC_URL + '/user_recipes_img/' + recipe.img}/>
										<div class='overlay'>
											<div class='overlay-title'>recipe.title</div>
										</div>
									</Link>
								</div>
							})
						}
					</div>
				</div>
			</div>
		);
	}
}



export default CookBanner;
