import React, { Component, useState } from 'react';
import { Link } from "react-router-dom";
import './forgot-password.sass';
import axios from './axios';


function Forgotpassword() {

	const [email, setEmail] = useState('');

	function sendEmail() {
		const data = {email: email}
		axios.post('/api/mail/pwd-reset', data)

	}

	return (
		<div className="reset-container">
			<div id="resetTitle">Password Reset</div>
			<div id="resetMsg">
				Enter the email of your account. We will then send you a link to reset your password.
			</div>
			<input 
				type="text" 
				name="email" 
				placeholder="Enter your email" 
				onChange={e => setEmail(e.target.value)}
				/>
			<input 
				className="hp" 
				type="text" 
				name="email_two" 
				placeholder="Enter your email" 
				/>
			<button type="submit" name="reset-request-submit">Send password reset</button>
		</div>
	);
}


export default Forgotpassword;
