import React, { Component, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './cookBanner.sass';
//import photoIcon from '../../public/user_profile_img/CHange_Name_16246155695775.jpeg';

const CookBanner = (props) => {
	const [recipeData, setRecipeData] = useState([]);

	useEffect(() => {

		const data = JSON.stringify({
			authid: props.authid, 
			currentRecipe: props.currentRecipe
		});

		axios.post('/api/recipes/get-cook-banner', data, { headers: {'Content-Type': 'application/json'} })
		.then((res) => {
			setRecipeData(res.data);
		})
		.catch((err) => {
			console.log(err);
		})
	}, [])

	if (!recipeData) {
		return null;
	} else {
		return(
			<div id='left-bannerN'>

				<div id='author-cardN'>
					<Link to='#'>
						<img src={process.env.PUBLIC_URL + '/user_profile_img/' + props.profileImg}/>
						<div id='auth-name'>{'By' + props.authName}</div>
					</Link>
				</div>

				<div id='moreByAuthor-load-container'>
					{/*<div id='arrow-down'></div>*/}
					{ 	
						recipeData.map((recipe, index) => 
							<div className='more-banner-cardN' key={index}>
								<Link to={{pathname: process.env.PUBLIC_URL + '/recipe-page/?rtitle=' + recipe.title}}>
									<img src={process.env.PUBLIC_URL + '/user_recipes_img/' + recipe.img} />
									<div className='titleInfo'>
										{recipe.title}
									</div>
								</Link>
							</div>
						)
					}
				</div>

			</div>
		);
	}
}



export default CookBanner;
