import React, { Component, useState, useEffect } from "react";
import BrowseCard from '../components/browseCard';
import './review-page.sass';
import axios from 'axios';
import { Link } from "react-router-dom";
import ContactForm from './'

const ReviewPage = () => {
	const [data, setData] = useState(null);
	const [render, setRender] = useState(0);
	const [openMail, setOpenMail] = useState(false);

	useEffect(() => {
		axios.get('/api/recipes/get-review')
		.then(res => {
			console.log('useEffect render');
			setData(res.data);
		})
		.catch(err => console.log(err));
	},[render])


	const approveRecipe = (rtitle, authid) => {
		const data = JSON.stringify({
			title: rtitle,
			authid: authid
		})

		axios.post('/api/recipes/approve', data, { headers: {'Content-Type': 'application/json'} } )
		.then(res => {
			setRender(render + 1);
		})
		.catch(err => console.log(err));
	}

	if (!data) {
		return null;
	} else {
		return (
			<div id="review-container">
				<table id="rev-table">
					<thead>
						<tr>
							<th id="title-auth">Author / User</th>
							<th id="title-recipe">Recipe</th>
							<th id="title-date">Uploaded (YY/DD/MM)</th>
							<th id="title-approve">Approve Recipe</th>
							<th id="title-contact">Contact Author</th>
						</tr>
					</thead>
					<tbody>
						{	data.length > 0 &&
							data.map((recipe, index) => {
								return (
									<tr key={index}>
										<td>{recipe.authid.name}</td>
										<td> <Link to={{pathname: process.env.PUBLIC_URL + '/recipe-page/?rtitle=' + recipe.title}}>{recipe.title}</Link> </td>
										<td> {recipe.uploadDate.slice(0, 10)} </td>
										<td> <button onClick={() => approveRecipe(recipe.title, recipe.authid._id) }>Approve</button> </td>
										<td> <button onClick={() => setOpenMail(true) }>Send Email</button> </td>
									</tr>
								);
							})
						}
				  	</tbody>
				</table>
					{	data.length < 1 &&
						<div id='noData'>No recipes for review</div>
					}


					{	openMail && 
						<ContactForm authName={null} authid={null}  />
					}

			</div>
		);
	}
}










export default ReviewPage;
