import React from 'react';
import './deleteRecipe.sass';

const DeleteRecipe = (props) => {


	if (!props.edit) {
		return null;
	} else {
		return (
			<React.Fragment>    
			    <div id='overlayDelete'></div>

	    		<button type='submit' name='deleteSubmit' id='deleteRecipe' onClick={confirmDelete()} >Delete Recipe</button>
	    		<div id='DeleteForm'>
					<p>Are you sure you want to delete INSERT RECIPE ? <br/> (this cannot be undone)</p>
					<div>
						<input type='hidden' name='rls' value='' autoComplete='off' />
						<button type='submit' name='confirmDelete'>Delete</button>
						<button type='button'>Back</button>
					</div>						
				</div>

	    	</React.Fragment>
		);
	} 
}

function confirmDelete() {
	document.querySelector('#DeleteForm').style.display = 'block';
	return 
}

export default DeleteRecipe;
