import React, { Component, useState } from 'react';
import { Link } from 'react-router-dom';
import './recipe-page.sass';
import { useParams } from 'react-router-dom';
import queryString from 'query-string';



class Recipepage extends Component {
	constructor(props) {
    	super(props);
    	this.state = {};
	}



	componentDidMount() {
		//let params = queryString.parse(this.props.location.search);
		let { id } = useParams();
		console.log(id);

		// fetch(USER_SERVICE_URL)
		// 	.then(res => res.json())
		// 	.then(result => {
		// 	    this.setState({users: result, isFetching: false})
		// 	})
		// 	.catch(e => {
		// 	    console.log(e);
		// 	    this.setState({...this.state, isFetching: false});
		// 	});
	}


	//console.log('/api/getrecipebyid/' + id);
	// const { id } = useParams();
	// fetch('/api/getrecipebyid/' + id)
	// 	.then(res => res.json())
	// 	.then(data => {
	// 		const [recipeData, updateRecipe] = useState(data);
	// 		console.log('got recipies', recipeData);
	// 	})
	// 	.catch(err => console.log(err));



	render() {

		return(

			<div className="recipe-page-container">
				<div id="recipe-content-banner">

					<div id="rec-container-1">

						<div id='recipe-img-container'>
							<img src={process.env.PUBLIC_URL + '/user_recipes_img/' } />
						</div>

						<div id="rec-container-1-title">{/*{this.props.title}*/}</div>

						<div id='about-container'>{/*{this.props.description}*/}</div>

						<div className='stat-name' id="recipePage-sym-container">display det symb</div>

						<div id='stat-container'>
							<div id="float-container">

								<div id='stat-name-container'>
									<div className='stat-name'>Time</div>
									<div className='stat-name'>Serves</div>
								</div>

								<div id="stat-number-container">
									<div className='stat-name'>{/*{this.props.cookTime}*/}</div>
									<div className='stat-name'>{/*{this.props.servings}*/}</div>
								</div>

							</div>
						</div>

					</div>

					<div id="rec-container-2">
						<div className='rec-container-headings'>
							INGREDIENTS
						</div>
						<ul>
							<li></li>
						</ul>
					</div>

					<div id="rec-container-3">
						<div className='rec-container-headings'>
							METHOD
						</div>
						<ol>
							<li></li>
						</ol>
					</div>

					<div id='rec-container-4'>
						<div className='rec-container-headings' id='note-title'>
							NOTES
						</div>
						<div className='note-container'>
							<span className='noteNum'></span>
							<span className='note'></span>
						</div>
					</div>

				</div>

				<div id="right-banner">
					diet banner display
					<div id="share-this-container">

						<div id="title">Share this recipe</div>

						<div id="ad-flexbox-container">
							<div><a href="#" className="fa fa-facebook"></a></div>
							<div><a href="#" className="fa fa-twitter"></a></div>
							<div><a href="#" className="fa fa-pinterest"></a></div>
							<div><a href="#" className="fa fa-envelope"></a></div>
						</div>

					</div>
				</div>

				<div id='rec-container-5'>

					<div className='rec-container-headings-5'>
						RECOMENDED
					</div>

					<div>
						<div className='more-banner-card more-recomended'>
							<Link to='/'>
								<img src='Media/user_recipe_images/".$img_thumb."' />
								<div className='overlay'>
									<div className='overlay-title'></div>
								</div>
							</Link>
						</div>
					</div>
				</div> 

			</div>
		);
	}
}


export default Recipepage;
