import React, { Component } from "react";
import './search-page.sass';
import DropMenu from '../components/dropMenu';
import MultiSelectMenu from '../components/multiSelectMenu';
import { mealTypes, dietOptions, cuisine } from '../searchOptions';
import ShowResults from '../components/show-results'
import axios from 'axios';

class Searchpage extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			show: false,
			results: [],
			isLoaded: false,
			error: false,
			searchText: '',
			diet: [],
			cuisine: [],
			mealType: [],
			time: null
		}
		this.searchFunction = this.searchFunction.bind(this);
		this.handleInput = this.handleInput.bind(this);
		//this.getParameterByName = this.getParameterByName.bind(this);
	}


	componentDidMount() {
		// inital keyword search
		//let keywords = this.getParameterByName('keywords');
		//let data = JSON.stringify({search: keywords});

		// axios.post('/api/recipes/search-component-mount', data, { headers: { 'Content-Type': 'application/json' } })
		// 	.then(res => {
		// 		this.setState({
		// 			results: res.data,
		// 			isLoaded: true
		// 		})
		// 	})
		// 	.catch(err => {
		// 		console.log(err.response.data);
		// 	});

		// // set search feild
		// this.setState({searchText: keywords});
	}

	searchFunction(input) {

		let data = JSON.stringify({
			diet: this.state.diet,
	       	cuisine: this.state.cuisine,
	       	mealType: this.state.mealType,
	       	time: this.state.time,
	       	searchText: this.props.search
		});

		// const postOptions = {
	 //        method: 'POST',
	 //        headers: { 'Content-Type': 'application/json' },
	 //        body: JSON.stringify(data)
	 //    };


		// fetch("/api/recipes/search/0", postOptions)
		// 	.then(res => res.json())
		// 	.then(
		// 		(data) => {
		// 			console.log(data);
		// 			this.setState({
		// 				results: data,
		// 				isLoaded: true,
		// 				error: false
		// 			})
		// 		},
		// 		(error) => {
		// 			console.log('error');
		// 			this.setState({
		// 				isLoaded: false,
		// 				error: error
		// 			})
		// 		}
		// 	)

		axios.post("/api/recipes/search", data, { headers: { 'Content-Type': 'application/json' } })
			.then(res => {
				console.log(res);
				this.setState({
					results: data,
					isLoaded: true,
					error: false
				})
			})
			.catch(err => console.log(err));
	}


	// getParameterByName(name, url = window.location.href) {
	//     name = name.replace(/[\[\]]/g, '\\$&');
	//     var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
	//         results = regex.exec(url);
	//     if (!results) return null;
	//     if (!results[2]) return '';
	//     return decodeURIComponent(results[2].replace(/\+/g, ' '));
	// }


	handleInput(stateName, value) {

		// if time selection
		if (stateName == 'time') {
			if (this.state.time === value) {
				this.setState({
					[stateName]: null
				},console.log(this.state.time));
			} else {
				this.setState({
					[stateName]: value
				},console.log(this.state.time));
			}
			return;
		}


		// if diet, mealType, or cuisine selection. Check if selection exists in state.diet array. If aleady exists, remove it. If it doesnt, append it to the state.diet
		if (stateName == 'diet') {
			var newState = this.state.diet;
			var cont = 1;
		} else if (stateName == 'cuisine') {
			var newState = this.state.cuisine;
			var cont = 1;
		} else if (stateName == 'mealType') {
			var newState = this.state.mealType;
			var cont = 1;
		}
		if (cont) {
			if (newState.includes(value)) {
				const index = newState.indexOf(value);
				newState.splice(index, 1);
			} else {
				newState.push(value);
			}

			this.setState({
				[stateName]: newState
			}, console.log(this.state));
		}


		// if searchText
		if (stateName == 'searchText') {
			this.setState({
				[stateName]: value
			}, console.log(this.state.searchText));
		}

	}




	render() {
		console.log(this.props.search);
		return (
			<React.Fragment>

				<div id="filter-container"> 

{/*						<div id="filterSearchContainer">
							<input 
								placeholder="Keywords" 
								type="text" 
								name="searchText" 
								value={this.state.searchText}
								onChange={(e) => this.handleInput(e.target.name, e.target.value)} 
							/>
							<button onClick={this.searchFunction} className="searchBtn" type="submit">Search</button>
						</div>*/}



						<div id="restrictions-body">

							<div className="filterDivs">
								<span className='restriction-label'>Dietary</span>
								<MultiSelectMenu 
									name='diet'
									options={dietOptions}
									state={this.state.diet}
									handleInput={(e) => this.handleInput(e.target.name, e.target.value)}
								/>
							</div>



							<div className="filterDivs">
					 			<span className='restriction-label'>Time</span>
								<label className="check-container"> 
									<span className='lt-sym'>&lt;</span> 30 min
									<input 
										name="time" 
										value='30' 
										type="checkbox"
										checked={this.state.time === "30"} 
										onChange={(e) => this.handleInput(e.target.name, e.target.value)}
									/>
									<span className="checkmark"></span>
								</label>
								<label className="check-container"> 
									<span className='lt-sym'>&lt;</span> 45 min
									<input 
										name="time" 
										value="45" 
										type="checkbox"
										checked={this.state.time === "45"}
										onChange={(e) => this.handleInput(e.target.name, e.target.value)} 
									/>
									<span className="checkmark"></span>
								</label>
								<label className="check-container"> 
									<span className='lt-sym'>&lt;</span> 60 min
									<input 
										name="time" 
										value="60" 
										type="checkbox"
										checked={this.state.time === "60"} 
										onChange={(e) => this.handleInput(e.target.name, e.target.value)}
									/>
									<span className="checkmark"></span>
								</label> 
							</div>



							<div className="filterDivs">
								<span className='restriction-label dropMenu-label'>Culinary</span>
								<DropMenu 
									name='cuisine'
									options={cuisine} 
									handleInput = {(e) => this.handleInput(e.target.name, e.target.value)}
									state={this.state.cuisine}
								/>
							</div>


							<div className="filterDivs">
								<span className='restriction-label dropMenu-label'>Meal Type</span>
								<DropMenu 
									name='mealType'
									options={mealTypes} 
									handleInput = {(e) => this.handleInput(e.target.name, e.target.value)}
									state={this.state.mealType}
								/>
							</div>

						</div>

				</div>



				{ this.props.results && 
					<ShowResults error={this.state.error} results={this.props.results} /> 
				}


			</React.Fragment>
		);
	}
}








export default Searchpage;
