import React, { Component } from 'react';
import './handleError.sass';

class HandleError extends Component {
	constructor(props) {
		super();
		this.state = {
			errMsg: props.errMsg,
			error: null
		}
	}



	render() {
		if (this.state.errMsg.length > 0) {
