function displayImg(file, fileName, fileObjURL, imageType) {
   // case of download from sever --> fileName only
   if (!file && fileName && !fileObjURL) var img = <img src={imagesRecipe(`./${fileName}`).default} />;
   // case of upload on client --> file fileName fileObjURL
   if (fileObjURL) var img = <img src={fileObjURL} />;

   return <div id='photo-container' className='clearfix'> {img} </div>;
}


