import React, { Component } from 'react';
import { Link } from "react-router-dom";
import BrowseCard from '../../components/browseCard';
// import qs from 'qs';
// import assert from 'assert';
import queryString from 'query-string';


class accountRecipes extends React.Component {
	constructor(props) {
		super();
		this.state = { 
			recipes: [],
			isLoaded: false,
			errMsg: ''
		};

		//this.handleUrlMsg = this.handleUrlMsg.bind(this);
	}

	getParameterByName(name, url = window.location.href) {
	    name = name.replace(/[\[\]]/g, '\\$&');
	    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
	        results = regex.exec(url);
	    if (!results) return null;
	    if (!results[2]) return '';
	    return decodeURIComponent(results[2].replace(/\+/g, ' '));
	}


	componentDidMount() {
		// read url params
		var url = window.location.href;
		var urlParam = 'msg';
		urlParam = urlParam.replace(/[\[\]]/g, '\\$&');
	    var regex = new RegExp('[?&]' + urlParam + '(=([^&#]*)|&|#|$)'),
	        results = regex.exec(url);
	    if (!results) return null;
	    if (!results[2]) return '';
	    const m = decodeURIComponent(results[2].replace(/\+/g, ' '));

	    console.log(m);

	    // if yes, setState

		fetch('/api/recipes/get-user-recipes')
			.then(res => res.json())
			.then((data) => {
				this.setState({
					recipes: data,
					isLoaded: true
				}, console.log(data));
			})
			.catch(err => console.log(err));
	}


	// handleUrlMsg(errMsg, urlParam){

	// 	if (m) {
	// 		const timer = setTimeout(() => {
	// 			this.setState({errMsg: ''});
	// 		}, 5500);
	// 		return <div className='bannerMsgBar'>{msg}</div>;
	// 	}
	// }

	render() {


		const m = this.getParameterByName('msg');
		if (m) { 
			this.setState({errMsg: m})
		}

		if (!this.state.isLoaded) {
			return null;
		} else {
			return (
				<React.Fragment>
					{/*{ this.handleUrlMsg(this.state.errMsg,  'msg') }*/}
					<div className="accountContent">
						<div id="cardContainer">
							{
								this.state.recipes.map((cardData, index) => 
									<BrowseCard 
										key={index} 
										rid={cardData._id} 
										img={cardData.img} 
										description={cardData.description} 
										author={cardData.authorName} 
										title={cardData.title}
									/>	
								)
							}

						</div>
					</div>
				</React.Fragment>
			);
		}
	}
}





export default accountRecipes;
