import React, { Component } from 'react';
import { Link } from "react-router-dom";
import BrowseCard from '../../components/browseCard';
// import qs from 'qs';
// import assert from 'assert';
import queryString from 'query-string';


class accountRecipes extends React.Component {
	constructor(props) {
		super();
		this.state = { 
			recipes: [],
			isLoaded: false,
			error: null
		};

		this.getParameterByName = this.getParameterByName.bind(this);
	}

	getParameterByName(name, url = window.location.href) {
	    name = name.replace(/[\[\]]/g, '\\$&');
	    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
	        results = regex.exec(url);
	    if (!results) return null;
	    if (!results[2]) return '';
	    return decodeURIComponent(results[2].replace(/\+/g, ' '));
	}


	componentDidMount() {
		// if (typeof this.props.location !== 'undefined') {console.log(this.props.location.state)}
		// console.log(this.props.location.state);
		//const m = qs.parse(this.props.location.search, { ignoreQueryPrefix: true }).msg;
		const m = getParameterByName(msg);
		console.log(m);

		// let params = queryString.parse(this.props.location.search)
		// console.log(params);

		fetch('/api/recipes/get-user-recipes')
			.then(res => res.json())
			.then((data) => {
				this.setState({
					recipes: data,
					isLoaded: true,
					error: false
				}, console.log(data));
			})
			.catch(err => console.log(err));
	}

	render() {
		if (!this.state.isLoaded) {
			return null;
		} else if (this.state.error) {
			return null;
		} else {
			return (
				<div className="accountContent">
					<div id="cardContainer">
						{
							this.state.recipes.map((cardData, index) => 
								<BrowseCard 
									key={index} 
									rid={cardData._id} 
									img={cardData.img} 
									description={cardData.description} 
									author={cardData.authorName} 
									title={cardData.title}
								/>	
							)
						}

					</div>
				</div>
			);
		}
	}
}





export default accountRecipes;
