import React, { Component, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './recipe-page.sass';
import { useParams } from 'react-router-dom';
import queryString from 'query-string';

// Add this in your component file
// require('react-dom');
// window.React2 = require('react');
// console.log(window.React1 === window.React2);


function Recipepage() {

	const [recipe, setRecData] = useState({
		authid: '',
		cuisine: '',
		description: '',
		diet: [],
		img: '',
		ingredients: [],
		method: [],
		notes: [],
		servings: '',
		title: '',
	});

	let { id } = useParams();


	useEffect(() => {
		fetch('/api/getrecipebyid/' + id)
			.then(res => res.json())
			.then(data => {
				// setRecData({
				// 	authid: data[0].authid,
				// 	cuisine: data[0].cuisine,
				// 	description: data[0].description,
				// 	diet: data[0].diet,
				// 	img: data[0].img,
				// 	ingredients: data[0].ingredients,
				// 	method: data[0].method,
				// 	notes: data[0].notes,
				// 	servings: data[0].servings,
				// 	title: data[0].title
				// });
				console.log(data);
			})
			.catch(err => console.log(err));
	}, []);




	return(

		<div className="recipe-page-container">
			<div id="recipe-content-banner">

				<div id="rec-container-1">

					<div id='recipe-img-container'>
						<img src={process.env.PUBLIC_URL + '/user_recipes_img/' } />
					</div>

					<div id="rec-container-1-title">{/*{recData[0].title}*/}</div>

					<div id='about-container'>{/*{recData[0].description}*/}</div>

					<div className='stat-name' id="recipePage-sym-container">display det symb</div>

					<div id='stat-container'>
						<div id="float-container">

							<div id='stat-name-container'>
								<div className='stat-name'>Time </div>
								<div className='stat-name'>Serves </div>
							</div>

							<div id="stat-number-container">
								<div className='stat-name'>{/*{recData[0].cookTime}*/}</div>
								<div className='stat-name'>{/*{recData[0].servings}*/}</div>
							</div>

						</div>
					</div>

				</div>

				<div id="rec-container-2">
					<div className='rec-container-headings'>
						INGREDIENTS
					</div>
					<ul>
						{/*{recData[0].ingredients.map((ingr) => <li> {ingr.qty + ingr.text} </li>)}	*/}
					</ul>
				</div>

				<div id="rec-container-3">
					<div className='rec-container-headings'>
						METHOD
					</div>
					<ol>
						<li></li>
					</ol>
				</div>

				<div id='rec-container-4'>
					<div className='rec-container-headings' id='note-title'>
						NOTES
					</div>
					<div className='note-container'>
						<span className='noteNum'></span>
						<span className='note'></span>
					</div>
				</div>

			</div>

			<div id="right-banner">
				diet banner display
				<div id="share-this-container">

					<div id="title">Share this recipe</div>

					<div id="ad-flexbox-container">
						<div><a href="#" className="fa fa-facebook"></a></div>
						<div><a href="#" className="fa fa-twitter"></a></div>
						<div><a href="#" className="fa fa-pinterest"></a></div>
						<div><a href="#" className="fa fa-envelope"></a></div>
					</div>

				</div>
			</div>

			<div id='rec-container-5'>

				<div className='rec-container-headings-5'>
					RECOMENDED
				</div>

				<div>
					<div className='more-banner-card more-recomended'>
						<Link to='/'>
							<img src='Media/user_recipe_images/".$img_thumb."' />
							<div className='overlay'>
								<div className='overlay-title'></div>
							</div>
						</Link>
					</div>
				</div>
			</div> 

		</div>
	);
}




export default Recipepage;
